import 'package:flutter/material.dart';
import 'package:my_flutter_project1/src/homepage.dart';
import 'package:my_flutter_project1/src/settingpage.dart';

class ProfilePage extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        backgroundColor: Colors.transparent,
        elevation: 0,
        leading: IconButton(
          icon: Icon(Icons.arrow_back),
          color: Colors.black,
          onPressed: () {
            Navigator.pop(context);
          },
        ),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment:
              CrossAxisAlignment.start, // Align items to the start
          children: [
            Row(
              children: [
                CircleAvatar(
                  radius: 40, // Specify the radius for CircleAvatar
                  backgroundImage: NetworkImage(
                      'data:image/png;base64,'), // Replace with your image URL
                ),
                SizedBox(width: 16), // Reduced width for better layout
                Expanded(
                  child: Text(
                    'User Name',
                    style: TextStyle(
                      fontSize: 24,
                      fontWeight: FontWeight.bold,
                    ),
                  ),
                ),
              ],
            ),
            SizedBox(height: 40),
            DropdownButton<String>(
              isExpanded: true,
              items: [
                DropdownMenuItem<String>(
                  value: 'Item1',
                  child: Text('Arabic'),
                ),
                DropdownMenuItem<String>(
                  value: 'Item2',
                  child: Text('English'),
                ),
              ],
              onChanged: (value) {
                // Handle dropdown change
              },
              hint: Text('Select Item'),
            ),
            SizedBox(height: 40),
            Row(
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: [
                Text(
                  'Dark Theme',
                  style: TextStyle(fontSize: 18),
                ),
                Switch(
                  value: false,
                  onChanged: (value) {
                    // Handle switch toggle
                  },
                ),
              ],
            ),
          ],
        ),
      ),
      bottomNavigationBar: BottomNavigationBar(
        items: const <BottomNavigationBarItem>[
          BottomNavigationBarItem(
            icon: Icon(Icons.home),
            label: 'Home',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.settings),
            label: 'Settings',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.person),
            label: 'Profile',
          ),
        ],
        onTap: (index) {
          if (index == 0) {
            Navigator.pushReplacement(
              context,
              MaterialPageRoute(builder: (context) => HomePage()),
            );
          } else if (index == 1) {
            Navigator.pushReplacement(
              context,
              MaterialPageRoute(builder: (context) => SettingsPage()),
            );
          } else if (index == 2) {
            // Do nothing for ProfilePage as it's already the current page
          }
        },
      ),
    );
  }
}
